mapper.update:
UPDATE article 
SET   
	article_head=#{article_head},
	article_title = #{article_title},
	article_content=#{article_content}
WHERE  article_no = #{article_no}

mapper.select:
SELECT Count(article_no) 
FROM   article 
WHERE  article_no > 0 and board_no= #{board_no}

SELECT article_no, article_head, article_title, article_content, regdate, hitcount, board_no, board_nm, user_nm, reply_count FROM (SELECT Ceil(rownum / #{perPageNum}) page, article_no, article_head, article_title, article_content, hitcount, regdate, board_no, board_nm, user_nm, reply_count FROM (SELECT article_no, article_head, article_title, article_content, To_char(article.regdate, 'YYYY-MM-DD') regdate, hitcount, board_no, (SELECT board_nm FROM board WHERE board_no = article.board_no) board_nm, (SELECT user_nm FROM users WHERE user_no = article.user_no) user_nm, (SELECT COUNT(article_no) FROM reply WHERE article_no = article.article_no) reply_count FROM article WHERE board_no= #{board_no}
ORDER BY article_no DESC)
) WHERE page = #{page}

mapper.update:
UPDATE ARTICLE 	
SET  HITCOUNT = HITCOUNT + 1
WHERE article_no = #{article_no}
