mapper.update:
UPDATE article 
SET   
	article_head=#{article_head},
	article_title = #{article_title},
	article_content=#{article_content}
WHERE  article_no = #{article_no}

mapper.select:
SELECT Count(article_no) 
FROM   article 
WHERE  article_no > 0 and board_no= #{board_no}

mapper.update:
UPDATE ARTICLE 	
SET  HITCOUNT = HITCOUNT + 1
WHERE article_no = #{article_no}
