       , user_pw
       , user_nm
       , user_birthday
       , user_addr
       , user_tel
       ) VALUES (
         users_seq.nextval
       , #{user.email}
       , #{user.pw}
       , #{user.name}
       , to_date(#{user.birthday})
       , #{addr}
       , #{tel}
       )

